import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import Header from "../component/header";
import HomeContent from "../component/homecontent";
import { connect } from "react-redux";
import { getCategoryMovies } from "../store/action/moviesActions";
import { doLogout } from "../store/action/signinAction";
import Category from "../component/category";

class Home extends Component {
  componentDidMount = async () => {
    this.props.getCategoryMovies();
    // console.log("response", this.props);
  };

  handleRequestCategory = async (categoryMovies) => {
    await this.props.history.replace("/" + categoryMovies);
    this.props.getCategoryMovies(categoryMovies);
  };

  render() {
    const isLoading = this.props.isLoading;
    return (
      <div>
        {/* {this.props.dataUser.isLogin ? ( */}
        <React.Fragment>
          <Header {...this.props} doLogout={() => this.props.doLogout()} />
          <HomeContent
            {...this.props}
            inputCategory={(categoryMovies) =>
              this.handleRequestCategory(categoryMovies)
            }
          />
          <hr />
          {isLoading ? (
            <div style={{ textAlign: "center" }}>Please wait . . . </div>
          ) : (
            <div>
              {this.props.listMovies.length > 0 ? (
                <h3>Category : {this.props.listMovies[0].Category}</h3>
              ) : (
                <div></div>
              )}
              <br />
              {this.props.listMovies.map((item, key) => (
                <Category
                  key={key}
                  title={item.Title}
                  poster={item.Poster}
                  synopsis={item.Synopsis}
                  year={item.Year}
                />
              ))}
            </div>
          )}
        </React.Fragment>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    listMovies: state.film.listMovies,
    isLoading: state.film.isLoading,
    logout: state.user.isLogin,
    isLogin: state.user.isLogin,
    dataUser: state.user,
  };
};
const mapDispatchToProps = {
  getCategoryMovies,
  doLogout,
};
export default connect(mapStateToProps, mapDispatchToProps)(Home);
