import axios from 'axios'

const baseUrl = "https://api-todofancy.herokuapp.com/api/movies";

export const getCategoryMovies = (categoryMovies) => {
    // alert("masuk get movie")
    return async (dispatch) => {
        // console.warn("masuk dispatch", d)
        await dispatch({
            type: "LOADING_MOVIES_CATEGORY"
        });
        const response = await axios.get(baseUrl);
        dispatch({
            type: "GET_MOVIES_CATEGORY",
            payload: response.data.movies.filter(item => item.Category == categoryMovies),
        });
        // console.log("cek response movie", response.data.movies.filter(item => item.Category = categoryMovies))
    }
};
