import React, { Component } from "react";
import axios from "axios";
// import { withRouter } from "react-router-dom";
import Header from "../component/header";
import { connect } from "react-redux";
import { doLogin, changeInputUser } from "../store/actions/signinAction";

class SignIn extends Component {
  postLogin = async () => {
    await this.props.doLogin();
    const isLogin = this.props.isLogin;
    console.log(isLogin);
    if (isLogin) {
      this.props.history.push("/");
    }
  };

  render() {
    console.log("cek props di sgin in", this.props);
    const message = this.props.location.state
      ? this.props.location.state.message
      : " ";

    return (
      <React.Fragment>
        <Header {...this.props} />
        <section
          className="content signin"
          style={{
            borderStyle: "double",
            borderWidth: "15px",
            borderColor: "CornflowerBlue",
            textAlign: "center",
            margin: "20px 500px 0 500px",
            padding: "10px 0 100px 0",
            backgroundColor: "aquamarine",
            borderRadius: "30px 0 30px 0",
          }}
        >
          <form onSubmit={(el) => el.preventDefault()}>
            <h4
              style={{
                color: "blue",
                fontWeight: "bolder",
              }}
            >
              SignIn
            </h4>
            <div>
              <input
                style={{
                  margin: "10px 0 10px 0",
                }}
                type="text"
                name="namaPengguna"
                placeholder="Username"
                onChange={(el) => this.props.changeInput(el)}
              />
            </div>
            <div>
              <input
                style={{
                  margin: "10px 0 10px 0",
                }}
                type="password"
                name="kataKunci"
                placeholder="Password"
                onChange={(el) => this.props.changeInput(el)}
              />
            </div>
            <button
              style={{
                height: "30px",
                width: "100px",
                backgroundColor: "CornflowerBlue",
                color: "white",
                margin: "50px 5px 0 0",
                borderRadius: "10px",
              }}
              onClick={() => this.postLogin()}
            >
              SignIn
            </button>
            <button
              style={{
                height: "30px",
                width: "100px",
                backgroundColor: "CornflowerBlue",
                color: "white",
                borderRadius: "10px",
              }}
              type="reset"
            >
              Reset
            </button>
            <p style={{ color: "red" }}>{message}</p>
          </form>
        </section>
      </React.Fragment>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    namaPengguna: state.user.namaPengguna,
    password: state.user.password,
    isLogin: state.user.isLogin,
    userData: state.user,
  };
};

const mapDispatchToProps = {
  changeInput: (el) => changeInputUser(el),
  doLogin: doLogin,
};

export default connect(mapStateToProps, mapDispatchToProps)(SignIn);
