import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import Header from "../component/header";
import { connect } from "react-redux";
import { getMovies } from "../store/actions/moviesActions";
import HomeContent from "../component/homecontent";

class Home extends Component {
  componentDidMount = async () => {
    const paramCategory = await this.props.match.params.category;
    this.props.getMovies(paramCategory); //category
  };
  render() {
    const isLoading = this.props.isLoading;
    console.log("isloading test");
    const listMovies = this.props.listMovies;

    const Moviiee = listMovies.filter((item) => {
      if (item.content !== null && item.image !== null) {
        return item;
      }
      return false;
    });

    return (
      <div>
        <React.Fragment>
          <div>
            <div>
              <Header
                handleRouter={(e) => this.handleRequestCategoryMovies(e)}
                getMovies={() => this.props.getMovies()}
                {...this.props}
              />
            </div>
            {/* {isLoading ? ( */}
            <div style={{ textAlign: "center" }}>Please wait . . . </div>
            {/* ) : ( */}
            <div class="my-5">
              {Moviiee.map((item, key) => (
                <HomeContent
                  key={key}
                  title={item.Title}
                  img={item.Poster}
                  content={item.Year}
                  publish={item.publishedAt}
                />
              ))}
            </div>
            )}
          </div>
        </React.Fragment>
        {/* ) : (
        <Redirect
          to={{
            pathname: "/signin",
          }}
        /> */}
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    movies: state.film.movies,
  };
};
const mapDispatchToProps = {
  getMovies,
};
export default connect(mapStateToProps, mapDispatchToProps)(Home);
