import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import Header from "../component/header";
import HomeContent from "../component/homecontent";
import { connect } from "react-redux";
import {
  getMovies,
  handleRequestCategoryRedux,
} from "../store/action/moviesActions";

class Home extends Component {
  componentDidMount = async () => {
    // const paramCategory = await this.props.match.params.category;
    // this.props.getMovies(paramCategory); //category
    const allMovies = this.props.getMovies(); //list kiri
    const fill = allMovies.Category.filter((Category) => Category == "action");
    console.log("response category", fill);
  };

  handleRequestCategory = async () => {
    // await this.props.history.replace("/category/" + categoryMovies);
    const allMovies = this.props.getMovies();
    allMovies.Category.filter((Category) => Category == "action");
    console.log("response category", allMovies);
  };

  render() {
    // console.log("response isi", this.props);
    return (
      <div>
        <Header />
        <HomeContent />
        {/* {MoviesCategory.map((item, key) => (
                        <Content1
                          key={key}
                          title={item.title}
                          img={item.urlToImage}
                          content={item.description}
                          url={item.url}
                          publish={item.publishedAt}
                        />
                      ))} */}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    listMovies: state.film.listMovies,
    isLoading: state.film.isLoading,
  };
};
const mapDispatchToProps = {
  getMovies,
  handleRequestCategoryRedux,
};
export default connect(mapStateToProps, mapDispatchToProps)(Home);
