import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import Header from "../component/header";
import { connect } from "react-redux";
import // getMovies,
// handleRequestCategoryMovies,
// handleRequestCategoryMoviesRedux,
"../store/actions/moviesActions";
import HomeContent from "../component/homecontent";

class Home extends Component {
  componentDidMount = async () => {
    const paramCategory = await this.props.match.params.category;
    this.props.getMovies(paramCategory);
  };

  handleRequestCategoryMovies = async (categoryName) => {
    await this.props.history.replace("/category/" + categoryName);
    const paramCategory = await this.props.match.params.category;
    this.props.handleRequestCategoryMoviesRedux(paramCategory);
  };

  render() {
    const isLoading = this.props.isLoading;
    console.log("isloading test");
    const listMovies = this.props.listMovies;

    const Moviiee = listMovies.filter((item) => {
      if (item.content !== null && item.image !== null) {
        return item;
      }
      return false;
    });

    return (
      <div>
        <React.Fragment>
          <div>
            <div>
              <Header
                handleRouter={(e) => this.handleRequestCategoryMovies(e)}
                getMovies={() => this.props.getMovies()}
                {...this.props}
              />
            </div>
            {/* {isLoading ? ( */}
            <div style={{ textAlign: "center" }}>Please wait . . . </div>
            {/* ) : ( */}
            <div class="my-5">
              {Moviiee.map((item, key) => (
                <HomeContent
                  handleRouter={(e) => this.handleRequestCategoryMovies(e)}
                  getMovies={() => this.props.getMovies()}
                  {...this.props}
                />
              ))}
            </div>
            )}
          </div>
        </React.Fragment>
        {/* ) : (
        <Redirect
          to={{
            pathname: "/signin",
          }}
        /> */}
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    listMovies: state.film.listMovies,
    isLoading: state.film.isLoading,
  };
};
const mapDispatchToProps = {
  getMovies,
  handleRequestCategoryMovies,
  handleRequestCategoryMoviesRedux,
};
export default connect(mapStateToProps, mapDispatchToProps)(Home);
