import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { doLogout } from "../store/actions/signinAction";

class Header extends React.Component {
  changeRouter = async (category) => {
    if (this.props.handleRouter) {
      this.props.handleRouter(category);
    } else {
      await this.props.history.replace("/news-category/" + category);
    }
  };

  //logout
  postSignout = async () => {
    await this.props.doLogout();
    const isLogin = this.props.logout;
    if (!isLogin) {
      this.props.history.push("/");
    }
  };
  render() {
    return (
      <div>
        <nav
          className="navbar navbar-expand-lg navbar-light"
          style={{
            backgroundColor: "AliceBlue",
          }}
        >
          <Link
            to="#"
            className="navbar-brand"
            value=""
            onClick={() => this.props.history.push("/")}
          >
            <img
              src={require("../image/logo192.png")}
              className="navbar-brand App-logo"
            />
          </Link>
          <Link
            to="#"
            className="nama-brand my-lg-4 mr-lg-2"
            value=""
            onClick={() => this.changeRouter("../")}
          >
            <h2 style={{ color: "DarkBlue" }}>Movie</h2>
          </Link>

          <button
            className="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div
            style={{
              fontWeight: "bolder",
            }}
            className="collapse navbar-collapse"
            id="navbarSupportedContent"
          >
            <ul className="navbar-nav mr-auto">
              <li className="nav-item ">
                <Link
                  to="#"
                  className="nav-link"
                  value="sport"
                  onClick={() => this.changeRouter("sport")}
                >
                  Home <span className="sr-only">(current)</span>
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  to="#"
                  className="nav-link"
                  value="science"
                  onClick={() => this.changeRouter("science")}
                >
                  Profile <span className="sr-only">(current)</span>
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  to="#"
                  className="nav-link"
                  value="technology"
                  onClick={() => this.changeRouter("technology")}
                >
                  Login <span className="sr-only">(current)</span>
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  to="#"
                  className="nav-link"
                  value="entertainment"
                  onClick={() => this.changeRouter("entertainment")}
                >
                  Hiburan <span className="sr-only">(current)</span>
                </Link>
              </li>
              <li className="nav-item dropdown">
                <a
                  className="nav-link dropdown-toggle"
                  href="."
                  id="navbarDropdown"
                  role="button"
                  data-toggle="dropdown"
                  aria-haspopup="true"
                  aria-expanded="false"
                >
                  Lainnya
                </a>
                <div
                  style={{
                    backgroundColor: "CadetBlue",
                    borderRadius: "5px",
                  }}
                  className="dropdown-menu"
                  aria-labelledby="navbarDropdown"
                >
                  <Link
                    className="nav-link"
                    value="health"
                    onClick={() => this.changeRouter("health")}
                  >
                    Kesehatan <span className="sr-only">(current)</span>
                  </Link>
                  <Link
                    className="nav-link"
                    value="business"
                    onClick={() => this.changeRouter("business")}
                  >
                    Bisnis <span className="sr-only">(current)</span>
                  </Link>
                </div>
              </li>
            </ul>
            <div
              className="collapse navbar-collapse"
              id="navbarSupportedContent"
            >
              <ul className="navbar-nav ml-auto">
                <li className="nav-item active">
                  {this.props.dataUser.isLogin ? (
                    <Link
                      className="nav-link"
                      onClick={() => this.postSignout()}
                    >
                      {" "}
                      Keluar <span className="sr-only">(current)</span>
                    </Link>
                  ) : (
                    <Link className="nav-link" to="/signin">
                      {" "}
                      Masuk <span className="sr-only">(current)</span>
                    </Link>
                  )}
                </li>
                <li className="nav-item">
                  {this.props.dataUser.isLogin ? (
                    <Link
                      className="nav-link"
                      onClick={() => this.props.history.push("/profile")}
                    >
                      {" "}
                      Profil <span className="sr-only">(current)</span>
                    </Link>
                  ) : (
                    <a className="nav-link" href=".">
                      Daftar
                    </a>
                  )}
                </li>
              </ul>
            </div>
          </div>
        </nav>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    dataUser: state.user,
    logout: state.user.isLogin,
  };
};

const mapDispatchToProps = {
  doLogout: doLogout,
};

export default connect(mapStateToProps, mapDispatchToProps)(Header);
