import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import Header from "../component/header";
import { connect } from "react-redux";

class Profile extends Component {
  myProfile = () => {
    console.log("ceck myprops profile", this.props);
    if (!this.props.dataUser.isLogin) {
      return (
        <Redirect
          to={{
            pathname: "/signin",
            state: {
              message: "maaf! password atau email yang anda masukkan salah",
            },
          }}
        />
      );
    } else {
      return (
        <React.Fragment>
          <Header {...this.props} />
          <section className="content">
            <h1
              style={{
                textalign: "center",
              }}
            >
              Profile
            </h1>
            <img src={this.props.dataUser.avatar} style={{ width: "100px" }} />
            <p>
              <label>Full Name:</label> {this.props.dataUser.username}
            </p>
            <p>
              <label>Email:</label> {this.props.dataUser.email}
            </p>
          </section>
        </React.Fragment>
      );
    }
  };
}
const mapStateToProps = (state) => {
  return {
    dataUser: state.user,
    isLogin: state.user.isLogin,
  };
};

export default connect(mapStateToProps)(Profile);
