const initialState = {
    movies: [],
    listMovies: [],
    isLoading: true,
};
export default function newsReducer(moviesState = initialState, action) {
    switch (action.type) {
        case "LOADING_MOVIES_CATEGORY":
            return {
                ...moviesState,
                isLoading: true
            };
        case "GET_LIST_MOVIES":
            return {
                ...moviesState,
                listMovies: action.payload,
                isLoading: false
            }
        case "LOADING_ERROR_MOVIES":
            return {
                ...moviesState,
                isLoading: false
            }
        case "GET_MOVIES_CATEGORY":
            return {
                ...moviesState,
                listMovies: action.payload,
                isLoading: false
            }
        default:
            return moviesState
    }
}
