import axios from 'axios'

const baseUrl = "https://api-todofancy.herokuapp.com/api/movies";

export const getMovies = () => {
    return async (dispatch) => {
        await
            dispatch({
                type: "LOADING_MOVIES",
            })
        const response = await axios.get(baseUrl);
        console.log("cek response", response.data.movies)
        dispatch({
            type: "GET_LIST_MOVIES",
            payload: response.data.movies.filter(Category => Category == "action"),
        });


    }
}

export const handleRequestCategoryRedux = (categoryMovies) => {
    return async (dispatch) => {
        await dispatch({
            type: "LOADING_MOVIES_CATEGORY"
        });
        const response = await axios.get(baseUrl);
        console.log("cek response movie", response.data.movies)
        dispatch({
            type: "GET_MOVIES_CATEGORY",
            payload: response.data.movies,
        });
    }
};
