import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { doLogout } from "../store/actions/signinAction";

class Header extends React.Component {
  changeRouter = async (category) => {
    if (this.props.handleRouter) {
      this.props.handleRouter(category);
    } else {
      await this.props.history.replace("/news-category/" + category);
    }
  };

  //logout
  postSignout = async () => {
    await this.props.doLogout();
    const isLogin = this.props.logout;
    if (!isLogin) {
      this.props.history.push("/");
    }
  };
  render() {
    return (
      <div>
        <nav
          className="navbar navbar-expand-lg navbar-light"
          style={{
            backgroundColor: "AliceBlue",
          }}
        >
          <Link
            to="#"
            className="navbar-brand"
            value=""
            onClick={() => this.props.history.push("/")}
          >
            <img
              src={require("../image/logo192.png")}
              className="navbar-brand App-logo"
            />
          </Link>
          <Link
            to="#"
            className="nama-brand my-lg-4 mr-lg-2"
            value=""
            onClick={() => this.changeRouter("../")}
          >
            <h2 style={{ color: "DarkBlue" }}>Movie</h2>
          </Link>

          <button
            className="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div
            style={{
              fontWeight: "bolder",
            }}
            className="collapse navbar-collapse"
            id="navbarSupportedContent"
          >
            <ul className="navbar-nav mr-auto">
              <li className="nav-item ">
                <Link
                  to="#"
                  className="nav-link"
                  value="home"
                  onClick={() => this.changeRouter("home")}
                >
                  Home <span className="sr-only">(current)</span>
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  to="#"
                  className="nav-link"
                  value="profile"
                  onClick={() => this.changeRouter("profile")}
                >
                  Profile <span className="sr-only">(current)</span>
                </Link>
              </li>
              <li className="nav-item">
                <Link
                  to="#"
                  className="nav-link"
                  value="login"
                  onClick={() => this.changeRouter("login")}
                >
                  Login <span className="sr-only">(current)</span>
                </Link>
              </li>
            </ul>
          </div>
        </nav>
      </div>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    dataUser: state.user,
    logout: state.user.isLogin,
  };
};

const mapDispatchToProps = {
  doLogout: doLogout,
};

export default connect(mapStateToProps, mapDispatchToProps)(Header);
