import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import Header from "../component/header";
import Content from "../component/homeContent";
import Content1 from "../component/homeContent1";
import { connect } from "react-redux";
import { getMovies } from "../store/actions/moviesActions";

class Home extends Component {
  componentDidMount = async () => {
    const paramCategory = await this.props.match.params.category;
    this.props.getMovies(paramCategory); //category
    this.props.getArticle(); //list kiri
  };
  render() {
    const isLoading = this.props.isLoading;
    console.log("isloading test");
    const listNews = this.props.listNews;
    console.log("list news masuk");
    const topHeadlines = listNews.filter((item) => {
      if (item.content !== null && item.image !== null) {
        return item;
      }
      return false;
    });

    return (
      <div>
        {this.props.dataUser.isLogin ? (
          <React.Fragment>
            <div>
              <div>
                <Header
                  handleRouter={(e) => this.handleRequestCategoryNews(e)}
                  getNews={() => this.props.getNews()}
                  keyword={this.props.search}
                  placeholder="ketik sesuatu"
                  {...this.props}
                />
              </div>

              <div className="container">
                <div className="row">
                  <div className="col-sm-5 my-5">
                    <Content newslist={this.props.articles} />
                  </div>
                  <div className="col-sm-7 my-5"></div>
                </div>
              </div>
            </div>
          </React.Fragment>
        ) : (
          <Redirect
            to={{
              pathname: "/signin",
            }}
          />
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    movies: state.film.movies,
  };
};
const mapDispatchToProps = {
  getMovies,
};
export default connect(mapStateToProps, mapDispatchToProps)(Home);
