import React, { Component } from "react";
import { Link } from "react-router-dom";

const HomeContent = (props, changeRouter) => {
  changeRouter = async (category) => {
    console.log("props", props);
    if (props.inputCategory) {
      await props.inputCategory(category);
    } else {
      props.hystory.replace("/" + category);
    }
  };
  return (
    <div
      className="card-group container"
      style={{ padding: "100px 0 20px 0", position: "relative" }}
    >
      <div className="card">
        <p style={{ textAlign: "center" }}>Romance</p>
        <img
          className="card-img-top ml-3"
          src={require("../media/romance.jpg")}
          alt="Card image cap"
          style={{ width: "250px", height: "370px" }}
        />
        <div className="card-body d-flex justify-content-center">
          <button
            onClick={() => changeRouter("romance")}
            value="romance"
            type="button"
            className="btn btn-primary"
            style={{ width: "100px", height: "40px" }}
          >
            See more
          </button>
        </div>
      </div>
      <div className="card">
        <p style={{ textAlign: "center" }}>Action</p>
        <img
          className="card-img-top ml-3"
          src={require("../media/action.jpg")}
          alt="Card image cap"
          style={{ width: "250px", height: "370px" }}
        />
        <div className="card-body d-flex justify-content-center">
          <button
            onClick={() => changeRouter("action")}
            value="action"
            type="button"
            className="btn btn-primary"
            style={{ width: "100px", height: "40px" }}
          >
            See more
          </button>
        </div>
      </div>
      <div className="card">
        <p style={{ textAlign: "center" }}>Fiction</p>
        <img
          className="card-img-top ml-3"
          src={require("../media/fiction.jpg")}
          alt="Card image cap"
          style={{ width: "250px", height: "370px" }}
        />
        <div className="card-body d-flex justify-content-center">
          <button
            onClick={() => changeRouter("fiction")}
            value="fiction"
            type="button"
            className="btn btn-primary"
            style={{ width: "100px", height: "40px" }}
          >
            Primary
          </button>
        </div>
      </div>
      <div className="card">
        <p style={{ textAlign: "center" }}>Commedy</p>
        <img
          className="card-img-top ml-3"
          src={require("../media/commedy.jpg")}
          alt="Card image cap"
          style={{ width: "250px", height: "370px" }}
        />
        <div className="card-body d-flex justify-content-center">
          <button
            onClick={() => changeRouter("comedy")}
            value="comedy"
            type="button"
            className="btn btn-primary"
            style={{ width: "100px", height: "40px" }}
          >
            Primary
          </button>
        </div>
      </div>
    </div>
  );
};

export default HomeContent;
