import React from "react";
import { Redirect } from "react-router-dom";
import Header from "../component/header";
import { connect } from "react-redux";

const Profile = (props) => {
  console.log(props);
  if (!props.dataUser.isLogin) {
    return (
      <Redirect
        to={{
          pathname: "/signin",
          state: {
            message: "maaf! password atau email yang anda masukkan salah",
          },
        }}
      />
    );
  } else {
    return (
      <React.Fragment>
        <Header {...this.props} />
        <section className="content">
          <h1
            style={{
              textalign: "center",
            }}
          >
            Profile
          </h1>
          <img src={props.dataUser.avatar} style={{ width: "100px" }} />
          <p>
            <label>Full Name:</label> {props.dataUser.username}
          </p>
          <p>
            <label>Email:</label> {props.dataUser.email}
          </p>
        </section>
      </React.Fragment>
    );
  }
};

const mapStateToProps = (state) => {
  return {
    dataUser: state.user,
    isLogin: state.user.isLogin,
  };
};

export default connect(mapStateToProps)(Profile);
