import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import Header from "../component/header";
import { connect } from "react-redux";
import { doLogout } from "../store/action/signinAction";

class Profile extends Component {
  postSignout = async () => {
    console.log("cek masuk logout");
    await this.props.doLogout();
    const isLogin = this.props.logout;
    console.log("cek islogin", isLogin);
    if (!isLogin) {
      this.props.history.push("/signin");
    }
  };

  render() {
    console.log("ceck myprops profile", this.props);
    if (!this.props.dataUser.isLogin) {
      return (
        <Redirect
          to={{
            pathname: "/signin",
            state: {
              message: "maaf! password atau email yang anda masukkan salah",
            },
          }}
        />
      );
    } else {
      return (
        <React.Fragment>
          <Header {...this.props} />
          <section className="content">
            <h1
              style={{
                textalign: "center",
              }}
            >
              Profile
            </h1>
            <img src={this.props.dataUser.avatar} style={{ width: "100px" }} />
            <p>
              <label>Full Name:</label> {this.props.dataUser.username}
            </p>
            <p>
              <label>Email:</label> {this.props.dataUser.email}
            </p>
          </section>
        </React.Fragment>
      );
    }
  }
}
const mapStateToProps = (state) => {
  return {
    dataUser: state.user,
    isLogin: state.user.isLogin,
  };
};

export default connect(mapStateToProps)(Profile);
