import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import Header from "../component/header";
import HomeContent from "../component/homecontent";
import { connect } from "react-redux";
import {
  getMovies,
  handleRequestCategoryRedux,
} from "../store/action/moviesActions";

class Home extends Component {
  componentDidMount = async () => {
    // const paramCategory = await this.props.match.params.category;
    // this.props.getMovies(paramCategory); //category
    this.props.getMovies(); //list kiri
    console.log("response", this.props);
  };

  handleRequestCategory = async (categoryName) => {
    await this.props.history.replace("/category/" + categoryName);
    const paramCategory = await this.props.match.params.category;
    this.props.handleRequestCategoryRedux(paramCategory);
  };

  render() {
    console.log("response isi", this.props);
    return (
      <div>
        <Header />
        <HomeContent />
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    listMovies: state.film.listMovies,
    isLoading: state.film.isLoading,
  };
};
const mapDispatchToProps = {
  getMovies,
  handleRequestCategoryRedux,
};
export default connect(mapStateToProps, mapDispatchToProps)(Home);
