import axios from "axios"
const signInUrl = "https://api-todofancy.herokuapp.com/api/auth"

export const doLogin = (props) => {
    console.log("cek masuk");
    return async (dispatch, getState) => {
        const bodyRequest = {
            username: getState().user.name,
            password: getState().user.password,
        }
        await axios
            .post(signInUrl, bodyRequest)
            .then(async (response) => {
                console.log("masuk api", response)
                dispatch({ type: "SUCCESS_LOGIN", payload: response.data.user_data });
            })
            .catch(function (error) {
                console.log(error)
            });
    };
};

export const changeInputUser = (el) => {
    return {
        type: "CHANGE_INPUT_USER",
        payload: el,
    };
};
export const doLogout = (el) => {
    return {
        type: "LOG_OUT",
        payload: el,
    };
};
