import React, { Component } from "react";
import { Link } from "react-router-dom";

class HomeContent extends React.Component {
  changeRouter = async (category) => {
    if (this.props.handleRouter) {
      this.props.handleRouter(category);
    } else {
      await this.props.history.replace("/category/" + category);
    }
  };
  render() {
    return (
      <div class="container">
        <div class="row">
          <div class="col">
            <img src={require("../media/romance.jpg")} />
            <Link onClick={() => this.changeRouter("romance")}>
              <p>Romance</p>
            </Link>
          </div>
          <div class="col">
            <img src={require("../media/action.jpg")} />
            <Link onClick={() => this.changeRouter("action")}>
              <p>Action</p>
            </Link>
          </div>
          <div class="col">
            <img src={require("../media/fiction.jpg")} />
            <Link onClick={() => this.changeRouter("fiction")}>
              <p>Fiction</p>
            </Link>
          </div>
          <div class="col">
            <img src={require("../media/commedy.jpg")} />
            <Link onClick={() => this.changeRouter("commedy")}>
              <p>Commedy</p>
            </Link>
          </div>
        </div>
      </div>
    );
  }
}

export default HomeContent;
