import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import Header from "../component/header";
import Content from "../component/content";
import { connect } from "react-redux";
import {
  getMovies,
  handleRequestCategoryMovies,
  handleRequestCategoryMoviesRedux,
} from "../store/actions/moviesActions";

class Category extends Component {
  componentDidMount = async () => {
    const paramCategory = await this.props.match.params.category;
    this.props.getMovies(paramCategory);
  };
  render() {
    const isLoading = this.props.isLoading;
    console.log("isloading test");
    const listMovies = this.props.listMovies;
    const Moviiee = listMovies.filter((item) => {
      if (item.content !== null && item.image !== null) {
        return item;
      }
      return false;
    });

    return (
      <div>
        <React.Fragment>
          <div>
            <div>
              <Header />
            </div>
            <div style={{ textAlign: "center" }}>Please wait . . . </div>
            <div class="my-5">
              {Moviiee.map((item, key) => (
                <Content
                  key={key}
                  title={item.Title}
                  img={item.Poster}
                  content={item.Synopsis}
                  publish={item.Year}
                />
              ))}
            </div>
          </div>
        </React.Fragment>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    listMovies: state.film.listMovies,
    isLoading: state.film.isLoading,
  };
};
const mapDispatchToProps = {
  getMovies,
  handleRequestCategoryMovies,
  handleRequestCategoryMoviesRedux,
};
export default connect(mapStateToProps, mapDispatchToProps)(Category);
