import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import Header from "../component/header";
import HomeContent from "../component/homecontent";
import { connect } from "react-redux";
import { getMovies } from "../store/action/moviesActions";

class Home extends Component {
  componentDidMount = async () => {
    const paramCategory = await this.props.match.params.category;
    // this.props.getNews(paramCategory); //category
    this.props.getMovies(); //list kiri
  };
  render() {
    return (
      <div>
        <Header />
        <HomeContent />
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    listMovies: state.film.listMovies,
    isLoading: state.news.isLoading,
  };
};
const mapDispatchToProps = {
  getMovies,
};
export default connect(mapStateToProps, mapDispatchToProps)(Home);
