const initialState = {
    movies: [],
    listNews: [],
    isLoading: true,
};
export default function newsReducer(moviesState = initialState, action) {
    switch (action.type) {
        case "GET_MOVIES":
            return {
                ...moviesState,
                articles: action.payload
            }
        case "LOADING_NEWS":
            return {
                ...moviesState,
                isLoading: true
            };
        case "GET_LIST_ARTICLE_NEWS":
            return {
                ...moviesState,
                listNews: action.payload,
                isLoading: false
            }
        case "LOADING_ERROR_NEWS":
            return {
                ...moviesState,
                isLoading: false
            }
        case "GET_NEWS_CATEGORY":
            return {
                ...moviesState,
                listNews: action.payload,
                isLoading: false
            }
        case "SEARCH_NEWS":
            return {
                ...moviesState,
                listNews: action.payload,
                isLoading: false
            }
        case "SEARCH_INPUT":
            return {
                ...moviesState,
                search: action.payload
            }
        default:
            return moviesState
    }
}
